document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".tab-button"),a=document.querySelectorAll(".tab-content");e.forEach(t=>{t.addEventListener("click",()=>{e.forEach(t=>t.classList.remove("active")),a.forEach(t=>{t.style.opacity="0",setTimeout(()=>{t.classList.remove("active")},150)}),t.classList.add("active"),setTimeout(()=>{document.getElementById(t.getAttribute("data-tab")).classList.add("active"),setTimeout(()=>{document.getElementById(t.getAttribute("data-tab")).style.opacity="1"},150)},150)})})});